    public class Api
    {
        public string Cedula { get; set; }
        public string Nombres { get; set; }
        public string Apellido1 { get; set; }
    }

    Api datos = new Api();
    string ced = "";

    private async Task resultados()
    {
        datos = await http.GetJsonAsync<Api>(" https://api.adamix.net/apec/cedula/" + ced);
    }

    string placa, marca, modelo, color, descripcion, lugar;

    bool dsx = false;
    int id;
    void Addcar()
    {
        if (dsx ==false)
        {
            var db = new Db();
            var car = new Vehiculo();
            var person = new Persona();

            car.placa = placa;
            car.marca = marca;
            car.modelo = modelo;
            car.color = color;
            car.descripcion = descripcion;


            db.Add(car);
            db.SaveChanges();

            person.cedula = datos.Cedula;
            person.nombre = datos.Nombres;
            person.apellido = datos.Apellido1;
            person.lugar = lugar;;
            db.Add(person);
            db.SaveChanges();
            limpiar();

        }

        else
        {
            var db = new Db();
            var person = new Persona();
            person.cedula = datos.Cedula;
            person.nombre = datos.Nombres;
            person.apellido = datos.Apellido1;
            person.lugar = lugar; ;
            db.Add(person);
            db.SaveChanges();
        }
    }

    void Addperson()
    {
        dsx = true;
        var db = new Db();
        var car = new Vehiculo();
        car.placa = placa;
        car.marca = marca;
        car.modelo = modelo;
        car.color = color;
        car.descripcion = descripcion;


        db.Add(car);
        db.SaveChanges();
        do
        {
            var person = new Persona();
            person.cedula = datos.Cedula;
            person.nombre = datos.Nombres;
            person.apellido = datos.Apellido1;
            person.lugar = lugar; ;
            db.Add(person);

        } while (dsx == false);
        db.SaveChanges();
        cleanp();
    }

    void limpiar()
    {
        placa = "";
        modelo = "";
        color = "";
        marca = "";
        descripcion = "";
    }

    void cleanp()
    {
        datos.Cedula = "";
        datos.Nombres = "";
        datos.Apellido1 = "";
        lugar = "";
    }


